import React from 'react'

 import AuthApiService from '../Auth-Service/api-auth-service'


export default class SignUp extends React.Component {

    static defaultProps = {
        onValidlRegistration: () => {}
    };

    constructor(props) {
        super(props);

        this.state = { 
            username: '',
            password: '',
            full_name:'',
            error: null

        };
    };



        // handle registration submission (POST new users). //
        submitRegistration = e => {
            e.preventDefault();
            const {user_name, password, first_name, last_name} = e.target; 

            this.setState({
                error: null
            });

            AuthApiService.postUser({
                user_name: user_name.value,
                password: password.value,
                first_name: first_name.value,
                last_name: last_name.value

            })
                .then(user => {
                    user_name.value = '';
                    password.value = '';
                    first_name.value = '';
                    last_name.value = '';
                    this.props.onValidlRegistration();
                })
                .then(() => {
                    window.location=`/`;
                    window.alert('Registered successfully. Please log in with your new credentials.');
                })
                .catch(res => {
                    this.setState({
                        error: window.alert('An unexpected error occurred. Please try again later.')
                    });
                });
        }    

    render(){

        return(
            <div className = "sign-up">
                <h2>You otter relax... Sign Up</h2>
                <form onSubmit = {this.submitRegistration}>
                    <p><input type="firstname" placeholder="First Name" name= 'user_name' id= 'user_name'/></p>
                    <p><input type="lastname" placeholder="Last Name"/></p>
                    <p><input type="username" placeholder="Username" name= 'user_name' id= 'user_name'/></p>
                    <p><input type="password" placeholder="Password" name= 'password' id= 'password'/></p>
                    <button>Sign Up</button>
                </form>
            </div>


        )
    }


}
