import React from 'react';
import getNumberofEntries from '../helper-funcs';
import journalContext from '../journal-context';
import moment from 'moment';
import { Link } from 'react-router-dom';
import './stats.css'


export default class Stats extends React.Component {

    static contextType = journalContext;


    render(){

    let posts = this.context.posts 

    let maxDateStat = () => {

        let array = [];

        if(!posts){
            return;
        }       
        else {

          posts.map( (posts , i) => {
              let formattedDate = moment(this.context.posts[i].modified).format('MM/DD/YYYY')
               array.push(formattedDate)
               return;

            })

            let max = new Date(Math.max(...array.map(date => new Date(date))))
            let formattedMax = moment(max).format('MM/DD/YYYY')
            return formattedMax;


            }
        } 

    let statsToDisplay =  maxDateStat() 

        return(
            <div className = 'stat-container'>
                {this.context.posts ? <div className = "stat">Your last Moments: {statsToDisplay}</div>: null }
                {this.context.posts ? <div className = "stat">{posts.length}</div>: null }
                <div className = "stat-button"><Link to = '/add-entry'class ='stat button-title'>Add a moment<button className = 'add-button'>+</button></Link></div>
            </div>

        )
    }
}
