import React from 'react'
import Edit from '../Edit-Form/Edit'
import {Link} from 'react-router-dom'
import journalContext from '../journal-context'
import config from '../config'
import AuthApiService from '../Auth-Service/api-auth-service'
import TokenService from '../Auth-Service/token-services'


const EntryBody = props => {
    const lines = props.entryData.map((line, index) => {
        return (
            <div key={index}>
                <h2>{line.title}</h2>
                <p>{line.body}</p>
                <button onClick={() => props.removePost(index)}>Delete</button>
                <Link to ='./edit'>Edit</Link>
            </div>
        )
    })

    return (
        <div>
            {lines}  
        </div>
    )
}

